export const useDeployERC721 = () => {
  const { address } = useAccount()
  const { isCorrectNetwork, networkName, baseNetworkName, switchToBaseNetwork } = useNetworkCheck()
  const { updateQuestProgress } = useQuestSystem()
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState(null)

  // Network validation and auto-switch function
  const validateAndSwitchNetwork = async () => {
    if (!isCorrectNetwork) {
      console.log(`üîÑ Wrong network detected! Switching from ${networkName} to ${baseNetworkName}...`)
      try {
        await switchToBaseNetwork()
        console.log('‚úÖ Successfully switched to Base network')
        // Wait a moment for the network switch to complete
        await new Promise(resolve => setTimeout(resolve, 1000))
      } catch (switchError) {
        console.error('‚ùå Failed to switch network:', switchError)
        throw new Error(`Failed to switch to Base network. Please manually switch to ${baseNetworkName} and try again.`)
      }
    }
  }

  const deployERC721 = async (name, symbol, imageFile) => {
    if (!address) {
      throw new Error('Wallet not connected')
    }

    // Validate and auto-switch network before proceeding
    await validateAndSwitchNetwork()

    setIsLoading(true)
    setError(null)

    try {
      console.log('üöÄ Processing ERC721 deployment request:', { name, symbol })

      // Step 1: Upload image to IPFS (only if provided)
      let imageUrl = null
      let metadataUrl = ''
      
      if (imageFile) {
        console.log('üì§ Uploading image to IPFS...')
        imageUrl = await uploadToIPFS(imageFile)
        console.log('‚úÖ Image uploaded to IPFS:', imageUrl)

        // Step 2: Create and upload metadata to IPFS
        console.log('üì§ Creating and uploading metadata to IPFS...')
        const metadata = createNFTMetadata(
          name,
          `${name} NFT Collection`,
          imageUrl,
          [
            { trait_type: "Collection", value: name },
            { trait_type: "Symbol", value: symbol }
          ]
        )
        metadataUrl = await uploadMetadataToIPFS(metadata)
        console.log('‚úÖ Metadata uploaded to IPFS:', metadataUrl)
      } else {
        console.log('‚è≠Ô∏è No image provided, using default metadata URL')
        metadataUrl = `https://api.example.com/metadata/${name.toLowerCase().replace(/\s+/g, '-')}`
      }

      // Step 3: Send fee to specified wallet
      const feeWallet = '0x7d2Ceb7a0e0C39A3d0f7B5b491659fDE4bb7BCFe'

      console.log('üí∞ Sending fee to wallet:', feeWallet)
      
      // Use a simple ETH transfer instead of sendTransaction
      const feeTxHash = await window.ethereum.request({
        method: 'eth_sendTransaction',
        params: [{
          from: address,
          to: feeWallet,
          value: '0x' + parseEther('0.00007').toString(16),
          gas: '0x5208', // 21000 gas for simple transfer
        }]
      })

      console.log('‚úÖ Fee transaction sent:', feeTxHash)

      // Wait for fee transaction confirmation
      await waitForTransactionReceipt(config, {
        hash: feeTxHash,
        confirmations: 1,
      })

      console.log('‚úÖ Fee transaction confirmed!')

      // Step 4: Deploy the actual ERC721 contract
      console.log('üöÄ Deploying ERC721 contract...')
      
      // Use original parameters (like the working commit)
      console.log('üìù Using original parameters:', { name, symbol })
      
      // TODO: Add bytecode here after manual deployment
      const ERC721_BYTECODE = "" // Add your bytecode here
      
      // Use sendTransaction with manual encoding for Farcaster compatibility (like ERC20)
      
      // Encode constructor parameters
      const { encodeAbiParameters, parseAbiParameters } = await import('viem')
      
      const constructorData = encodeAbiParameters(
        parseAbiParameters('string name, string symbol, string baseTokenURI'),
        [name, symbol, metadataUrl]
      )
      
      const deployData = ERC721_BYTECODE + constructorData.slice(2)
      
      const deployTxHash = await sendTransaction(config, {
        data: deployData,
        gas: 2000000n, // 2M gas limit for contract deployment
      })
      
      console.log('‚úÖ Deploy transaction sent:', deployTxHash)
      
      // Wait for deploy confirmation
      const deployReceipt = await waitForTransactionReceipt(config, {
        hash: deployTxHash,
        confirmations: 1,
      })
      
      console.log('‚úÖ ERC721 contract deployed successfully!')
      console.log('üìÑ Contract Address:', deployReceipt.contractAddress)

      // Award XP for successful ERC721 deployment
      try {
        console.log('üéâ Awarding 100 XP for ERC721 deployment!')
        await addXP(address, 100, 'ERC721 Deployment')
      } catch (xpError) {
        console.error('‚ùå Failed to add XP:', xpError)
      }

      // Update quest progress for ERC721 deployment (independent of XP)
      try {
        console.log('üéØ Updating quest progress for ERC721 deployment!')
        await updateQuestProgress('erc721Deployed', 1)
      } catch (questError) {
        console.error('‚ùå Failed to update quest progress:', questError)
      }

      // Record transaction
      try {
        await recordTransaction({
          wallet_address: address,
          transaction_type: 'ERC721_DEPLOY',
          transaction_hash: deployTxHash,
          contract_address: deployReceipt.contractAddress,
          amount: '0.00007',
          currency: 'ETH',
          status: 'success',
          game_type: 'ERC721 Deployment',
          metadata: {
            name,
            symbol,
            imageUrl: imageUrl || null,
            metadataUrl
          }
        })
      } catch (recordError) {
        console.error('‚ùå Failed to record transaction:', recordError)
      }

      return {
        contractAddress: deployReceipt.contractAddress,
        imageUrl: imageUrl || null,
        metadataUrl,
        deployTxHash
      }
    } catch (err) {
      console.error('‚ùå ERC721 deployment failed:', err)
      setError(err.message || 'Failed to deploy ERC721 contract')
      throw err
    } finally {
      setIsLoading(false)
    }
  }

  return { deployERC721, isLoading, error }
}
